/*
   generates links between all linked nodes and
   returns a link object with link properties.
*/

function generateLinks(data) {
  const links = []; //array of all node links

  data.forEach((item, i) => {
    if (!item.target || (item === data[data.length-1])) {
      //if the node does not have a target OR is the last node of the array, skip generating link
      return
    } else {
      item.target.forEach((n) => {
        links.push({source:item, target:data.find(x => x.id === n), color:item.lineColor ? item.lineColor : item.color ? item.color : null, width:item.lineWidth});
      });
    }
  });
  return links;
};

export default generateLinks;
