/*
  function for updating source JSON

  if data:
  - data, setData, newData, src, setSrc, setHistory, history, activeData
  - newSrc = src;
  - newSrc.instants[activeData] = data;
  - setHistory([...history, newSrc])
  - setData(newData);
  - setToggle(null);

  if src:
  - src, setSrc, newData, key, setHistory, history
  - setHistory([...history, src])
  - setSrc(newData);
  - setData()
  - setZones()
  - setScales()
  - setToggle(null);
*/

function updateSrc(setHistory, history, src, setSrc, setToggle, activeData, data, newData) {
  setHistory([...history, data]);
  setSrc(newData);
  setToggle(null);
};

export default updateSrc;
